import React, { useState } from "react";
import ToDoList from "./ToDoList";

const ToDo = () => {
  const [inputData, setinputData] = useState("");
  const [item, setItemList] = useState([]);

  const inputItem = (e) => {
    setinputData(e.target.value);
  };

  const addItem = () => {
    setItemList((prevItem) => {
      return [...prevItem, inputData];
    });
    setinputData("");
  };

  const deleteItem = (id) => {
    console.log("deleted");
    setItemList((prevItem) => {
      return prevItem.filter((arrElement, index) => {
        return index !== id;
      });
    });
  };
  return (
    <div>
      <h1> ToDo List </h1>
      <div>
        <input
          type="text"
          placeholder="add a item"
          value={inputData}
          onChange={inputItem}
        />
        <button variant="primary" onClick={addItem}>
          Add item
        </button>
      </div>

      <div>
        <table>
          <thead>
            <tr>
              <th>ToDo</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {item.map((itemval, index) => {
              return (
                <ToDoList
                  itemval={itemval}
                  key={index}
                  id={index}
                  onSelect={deleteItem}
                />
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ToDo;
