import React, { useState } from "react";
import ToDoList from "./ToDoList";
import { Button,Form,Container,Row,Col } from 'react-bootstrap';

const ToDo = () => {
  const [inputData, setinputData] = useState("");
  const [item, setItemList] = useState([]);

  const inputItem = (e) => {
    setinputData(e.target.value);
  };

  const addItem = () => {
    if(inputData)
    {
      setItemList((prevItem) => {
        return [...prevItem, inputData];
      });
      setinputData("");
    }

  };

  const deleteItem = (id) => {
    setItemList((prevItem) => {
      return prevItem.filter((arrElement, index) => {
        return index !== id;
      });
    });
  };
  return (
    <div>      
      <Container>
        <Row>
          <Col>
            <div>
              <Form>
                <Form.Group controlId="addTodo">
                  <Form.Label>Add Todo</Form.Label>
                  <Form.Control type="text" placeholder="Add a todo" value={inputData} onChange={inputItem} required={true} maxLength={20}/>                  
                </Form.Group>
                <Button variant="success" type="submit" onClick={addItem}>
                  Add
                </Button>
              </Form>
            </div>
          </Col>
          <Col>
            <div>
              <h3> ToDo List</h3>
              <table>
                <thead>
                  <tr>
                    <th>ToDo</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {item.map((itemval, index) => {
                    return (
                      <ToDoList
                        itemval={itemval}
                        key={index}
                        id={index}
                        onSelect={deleteItem}
                      />
                    );
                  })}
                </tbody>
              </table>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ToDo;
